      "description": "Decision: 'use_existing' or 'create_new'."
    },
    "collectionId": {
      "type": "number",
      "description": "The ID of the existing collection. Omit or set to null if creating new."
    },
    "newCollectionName": {
      "type": "string",
      "description": "Name for the new collection. Omit or set to null if using existing."
    },
    "description": {
      "type": "string",
      "description": "A short description of the content."
    }
