const collections = $input.item.json.response || [];
const userUrl = $input.item.json.chatInput;
const pageTitle = $input.item.json.pageTitle || '';
const pageDescription = $input.item.json.pageDescription || '';
const pageContent = $input.item.json.pageContent || '';

// Build context string
const contextString = collections.map(c => 
  `ID: ${c.id} | Name: "${c.name}" | Description: "${c.description || 'N/A'}"`
).join('\n');

return {
  categoriesContext: contextString,
  chatInput: userUrl,
  pageTitle: pageTitle,
  pageDescription: pageDescription,
  pageContent: pageContent
};

const aiOutput = $input.item.json.output || $input.item.json.text || "";
// וודא שאתה מושך את ה-URL מהמקום הנכון (Merge או Parser)
const originalUrl = $('Merge').first().json.chatInput ? $('Merge').first().json.chatInput.trim() : ""; 
const collections = $('get collections').first().json.response;

const jsonMatch = aiOutput.match(/\{[\s\S]*\}/);

// הגנה 1: אם אין JSON בכלל
if (!jsonMatch) {
  return { 
    action: 'use_existing',
    collectionId: 4, 
    description: "AI Error: No JSON returned",
    url: originalUrl
  };
}

let parsedData;
try {
  parsedData = JSON.parse(jsonMatch[0]);
} catch (e) {
  // הגנה 2: אם ה-JSON שבור
  return { 
    action: 'use_existing',
    collectionId: 4, 
    description: "AI Error: Invalid JSON",
    url: originalUrl
  };
}

// === הטיפול ב-Use Existing ===
if (parsedData.action === 'use_existing') {
  const requestedId = parseInt(parsedData.collectionId);
  const exists = collections.find(c => c.id === requestedId);

  if (exists) {
    // הכל תקין, ה-ID אמיתי
    return {
      action: 'use_existing',
      collectionId: requestedId,
      description: parsedData.description,
      url: originalUrl
    };
  } else {
    // הגנה 3: ה-AI המציא מספר (כמו 123)
    // במקרה כזה אנחנו "מענישים" אותו ושולחים ל-Personal, אבל לא שוברים את האוטומציה
    return {
      action: 'use_existing',
      collectionId: 4, // Safety Fallback
      description: parsedData.description + ` (AI tried fake ID: ${requestedId})`,
      url: originalUrl
    };
  }
}

// === הטיפול ב-Create New ===
if (parsedData.action === 'create_new') {
  return {
    action: 'create_new',
    newCollectionName: parsedData.newCollectionName || "General",
    categoryDescription: parsedData.categoryDescription || "New Category",
    linkDescription: parsedData.linkDescription || parsedData.description,
    url: originalUrl
  };
}

// הגנה 4: פעולה לא מוכרת
return { 
  action: 'use_existing',
  collectionId: 4, 
  description: "AI Error: Unknown Action",
  url: originalUrl
};

const item = $input.item.json;

// 1. ניקוי ה-URL (הסרת לוכסן בסוף אם קיים)
let cleanUrl = item.url.trim();
if (cleanUrl.endsWith('/')) {
  cleanUrl = cleanUrl.slice(0, -1);
}

// 2. בניית ה-Payload המדויק ל-Linkwarden
// המבנה המקונן שהתעקשת עליו
const payload = {
  url: cleanUrl,
  description: item.description,
  collection: {
    id: parseInt(item.collectionId) // וידוא שזה מספר ולא טקסט
  },
  tags: [] // אופציונלי, לפעמים מונע שגיאות
};

return {
  json: payload
};

// Get the HTML - try multiple possible properties
const html = $input.item.json.body || 
             $input.item.json.data || 
             $input.item.json || 
             '';

const url = $input.item.json.url || '';

// If still no HTML, return empty
if (!html || html.length < 50) {
  return {
    url: url,
    pageTitle: '',
    pageDescription: '',
    pageContent: 'Unable to fetch page content',
    fetchError: true
  };
}

// Extract title
const titleMatch = html.match(/<title[^>]*>([^<]+)<\/title>/i);
const title = titleMatch ? titleMatch[1].trim().substring(0, 200) : '';

// Extract meta description
const descMatch = html.match(/<meta\s+name=["']description["']\s+content=["']([^"']+)["']/i);
const description = descMatch ? descMatch[1].trim().substring(0, 300) : '';

// Extract first meaningful text
let cleanHtml = html
  .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
  .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
  .replace(/<[^>]+>/g, ' ')
  .replace(/\s+/g, ' ')
  .trim();

const pageContent = cleanHtml.substring(0, 300);

return {
  url: url,
  pageTitle: title || 'Page loaded',
  pageDescription: description || 'Cooking recipe content',
  pageContent: pageContent
};

const newCollectionId = $input.item.json.response.id;

const originalData = $('parser New / Existing').first().json;


let cleanUrl = originalData.url ? originalData.url.trim() : originalData.chatInput.trim(); 
if (cleanUrl.endsWith('/')) {
  cleanUrl = cleanUrl.slice(0, -1);
}


const payload = {
  url: cleanUrl,

  description: originalData.linkDescription || originalData.description, 

  collection: {
    id: parseInt(newCollectionId)
  },
  tags: []
};

return {
  json: payload
};
